const hamburger=document.querySelector("#hamburger"),menu=document.querySelector("#menu"),hamburgerToggle=()=>{hamburger.classList.toggle("is-active"),menu.classList.toggle("visible"),hamburger.style.outline="none"};hamburger.addEventListener("click",hamburgerToggle),document.addEventListener("keydown",e=>{27===e.keyCode&&(hamburger.classList.remove("is-active"),menu.classList.remove("visible"))});